INSERT INTO `documents` (`id`, `title`, `doctype`, `divtype`, `subtype`, `rhyme`, `meter`, `ispoem`, `text`, `url`) VALUES
(1, 'Account of a Tour on the Continent', 'apparatus', 'apparatus', '', '', '', 0, '\r\n        <body>\r\n            <div type="apparatus">\r\n                <head type="title">Account of a Tour on the Continent</head>\r\n                <div type="section" xml:id="WITNESSES">\r\n    			<head type="section">Witnesses</head>\r\n    				<p><ref type="witness" target="account_of_a_tour_on_the_continent_msia_g1.php">Account of a Tour on the Continent (MS IA g1)</ref>\r\n                        <list>\r\n                            <item><ref type="witness" target="calais_poem_msia_g1.php">&#x201C;Calais&#x201D; [poem]</ref></item>\r\n                            <item><ref type="witness" target="cassel_poem_msia_g1.php">[&#x201C;Cassel&#x201D;] [poem]</ref></item>\r\n                            <item><ref type="witness" target="lille_poem_msia_g1.php">&#x201C;Lille&#x201D; [poem]</ref></item>\r\n                            <item><ref type="witness" target="brussels_poem_msia_g1.php">&#x201C;Brussels&#x201D; [poem]</ref></item>\r\n                            <item><ref type="witness" target="the_meuse_poem_msia_g1.php">&#x201C;The Meuse&#x201D; [&#x201C;Cassel,&#x201D; poem]</ref></item>\r\n                        </list>\r\n    				</p>             \r\n                    <p><ref type="witness" target="account_of_a_tour_on_the_continent_msia_g2.php">Account of a Tour on the Continent (MS IA g2)</ref>\r\n                        <list>\r\n                            <item><ref type="witness" target="calais_prose_msia_g2.php">&#x201C;Calais&#x201D; [prose]</ref></item>\r\n                            <item><ref type="witness" target="passing_the_alps_poem_msia_g2.php">&#x201C;Passing the Alps&#x201D; [poem]</ref></item>\r\n                            <item><ref type="witness" target="milan_cathedral_poem_msia_g2.php">&#x201C;Milan Cathedral&#x201D; [poem]</ref></item>\r\n                            <item><ref type="witness" target="andernacht_poem_msia_g2.php">&#x201C;Andernacht&#x201D; [poem]</ref></item>\r\n                            <item><ref type="witness" target="st_goar_poem_msia_g2.php">&#x201C;St. Goar&#x201D; [poem]</ref></item>\r\n                        </list>\r\n                    </p>\r\n                    <p><ref type="witness" target="account_of_a_tour_on_the_continent_msviii.php">Account of a Tour on the Continent (MS VIII)</ref>\r\n                        <list>\r\n                            <item><ref type="witness" target="oh_are_there_spirits_can_there_be_poem_msviii.php">&#x201C;Oh are there spirits, can there be&#x201D; [poem]</ref></item>\r\n                            <item><ref type="witness" target="source_of_the_arveron_prose_msviii.php">&#x201C;Source of the Averon&#x201D; [prose]</ref></item>\r\n                            <item><ref type="witness" target="i_woke_to_hear_the_lullaby_poem_msviii.php">&#x201C;I woke to hear the lullaby&#x201D; [&#x201C;The Arve at Chamouni&#x201D;] [poem]</ref></item>\r\n                            <item><ref type="witness" target="andernacht_prose_msviii.php">&#x201C;Andernacht&#x201D; [prose]</ref></item>\r\n                            <item><ref type="witness" target="st_goar_prose_msviii.php">&#x201C;St. Goar is the least and sweetest place&#x201D; [prose]</ref></item>\r\n                            <item><ref type="witness" target="there_is_a_charmed_peace_that_aye_poem_msviii.php">&#x201C;There is a charmed peace, that aye&#x201D; [&#x201C;The Alps from Schaffhausen&#x201D;] [poem]</ref></item>\r\n                            <item><ref type="witness" target="via_mala_poem_msviii.php">&#x201C;Via Mala&#x201D; [poem]</ref></item>\r\n                            <item><ref type="witness" target="splugen_poem_msviii.php">&#x201C;Splugen&#x201D; [poem]</ref></item>\r\n                            <item><ref type="witness" target="the_summit_poem_msviii.php">&#x201C;The Summit&#x201D; [poem]</ref></item>\r\n                            <item><ref type="witness" target="the_descent_poem_msviii.php">&#x201C;The Descent&#x201D; [poem]</ref></item>\r\n                            <item><ref type="witness" target="the_meuse_prose_msviii.php">&#x201C;The Meuse&#x201D; [prose]</ref></item>\r\n                            <item><ref type="witness" target="cologne_prose_msviii.php">&#x201C;Cologne&#x201D; [prose]</ref></item>\r\n                            <item><ref type="witness" target="oh_the_morn_looked_bright_on_hill_and_dale_poem_msviii.php">&#x201C;Oh the morn looked bright on hill and dale&#x201D; [&#x201C;The Black Forest&#x201D;] [poem]</ref></item>\r\n                            <item><ref type="witness" target="it_was_a_wide_and_stretchy_sweep_prose_msviii.php">&#x201C;It was a wide and stretchy sweep&#x201D; [prose]</ref></item>\r\n                            <item><ref type="witness" target="part_of_brussels_prose1_msviii.php">&#x201C;Part of Brussels&#x201D; [prose, part 1]</ref></item>\r\n                            <item><ref type="witness" target="oh_softly_blew_the_morning_breeze_poem_msviii.php">&#x201C;Oh softly blew the morning breeze&#x201D; [&#x201C;Chiavenna&#x201D;] [poem]</ref></item>\r\n                            <item><ref type="witness" target="part_of_brussels_prose2_msviii.php">&#x201C;Part of Brussels&#x201D; [prose, part 2]</ref></item>\r\n                            <item>More to Come</item>\r\n                        </list>\r\n                    </p>\r\n                    <p><ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">Account of a Tour on the Continent (MS IX)</ref>\r\n                        <list>\r\n                            <item><ref type="witness" target="calais_poem_msix.php">&#x201C;Calais&#x201D; [poem]</ref></item>   \r\n                        </list>\r\n                    </p>\r\n                </div>\r\n                <div type="section" xml:id="TITLE">\r\n                    <head type="section">Title</head>\r\n                    <p>In <ref type="note" subtype="bibliographical" target="poems_1891_note.php"><hi rend="italic">Poems</hi> (1891)</ref>, \r\n                        <persName corresp="#WGC">W. G. Collingwood</persName> printed the title, \r\n                        Account of a Tour on the Continent, without square brackets, his symbol for declaring editorial intervention. \r\n                        Nonetheless, the title apparently originated with him, as <persName corresp="#JR">Ruskin</persName> assigned no title to the \r\n                        sequence as a whole in any of the extant manuscripts \r\n                        (see <ref type="bibliography" target="bibliography.php#POEMS4D1891"><hi rend="italic">Poems</hi> [4<hi rend="superscript">o</hi>, 1891]</ref>, 1: 119, 281; \r\n                        <ref type="bibliography" target="bibliography.php#POEMS8D1891"><hi rend="italic">Poems</hi> [8<hi rend="superscript">o</hi>, 1891]</ref>, 1: 119, 282). \r\n                        What titles <persName corresp="#JR">Ruskin</persName> supplied apply only to individual verse \r\n                        and prose sections of the work. Accordingly, in <hi rend="italic">ERM</hi>, \r\n                        when the main title is fully spelled out, it is given without quotation \r\n                        marks, in keeping with the guidelines given in <ref type="note" subtype="bibliographical" target="title_citation_note.php">The System of Title Citation for Works</ref>. \r\n                        In this apparatus, however, for convenience in the course of discussion, a short title is used with quotation marks (i.e., the &#x0201C;Account&#x0201D;) \r\n                        in order to avoid confusion with surrounding text.</p>\r\n                    <p>In another, more minor discrepancy respecting the editorial title, both \r\n                        <ref type="note" subtype="bibliographical" target="poems_1891_note.php"><hi rend="italic">Poems</hi> (1891)</ref> and the \r\n                        <ref type="note" subtype="bibliographical" target="library_edition_note.php">Library Edition</ref> cite the title in their respective tables of contents as \r\n                        &#x201C;Account of a Tour on the Continent in <date when="1833">1833</date>,&#x201D; but the title pages of the work in these editions lack \r\n                        the extension containing the date \r\n                        (<ref type="bibliography" target="bibliography.php#POEMS4D1891"><hi rend="italic">Poems</hi> [4<hi rend="superscript">o</hi>, 1891]</ref>, 1:xv, 119; \r\n                        <ref type="bibliography" target="bibliography.php#POEMS8D1891"><hi rend="italic">Poems</hi> [8<hi rend="superscript">o</hi>, 1891]</ref>, 1:xix, 119; \r\n                        <ref type="bibliography" target="bibliography.php#JRWORKSLE">Ruskin, <hi rend="italic">Works</hi></ref>, 2: xii, 340).</p>\r\n                    <p><persName corresp="#WGC">Collingwood</persName> also invented titles for some individual sections of verse, untitled sections that he took from \r\n                        <persName corresp="#JR">Ruskin</persName>&#x02BC;s  \r\n                        <ref type="witness" target="account_of_a_tour_on_the_continent_msviii.php">MS VIII draft of the &#x0201C;Account&#x0201D;</ref> in \r\n                        order to supplement the main copytext of poems taken from the <ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">MS IX fair copy</ref>. \r\n                        These editorial titles are as follows:\r\n                        <list type="unordered">\r\n                            <item>TO COME</item>\r\n                            <!-- DCH: TO BE SUPPLIED -->\r\n                        </list>\r\n                    </p>\r\n                    <p>When two selections from the &#x201C;Account&#x201D; were published in the literary annual, \r\n                        <ref type="note" subtype="bibliographical" target="friendships_offering_note.php"><hi rend="italic">Friendship&#x02BC;s Offering</hi></ref> \r\n                        (see <ref type="internal" target="COMPPUB">Composition and Publication</ref>), \r\n                        the selections were subsumed together under the title, &#x201C;Fragments from a Metrical Journal&#x201D; \r\n                        (<ref type="bibliography" target="bibliography.php#FRIENDSHIPSOFFERING1835"><hi rend="italic">Friendship&#x02BC;s Offering; and Winter&#x02BC;s Wreath . . . for MDCCCXXXV</hi></ref>, 317&#x2013;19). \r\n                        The responsibility for assigning this title is unknown.</p>\r\n                </div>\r\n                <div type="section" xml:id="GENRE">\r\n                    <head type="section">Genre</head>\r\n                    <p>Composite verse and prose illustrated travelogue. As proposed in \r\n                        <ref type="internal" target="COMPPUB">Composition and Publication</ref>, \r\n                        <persName corresp="#JR">Ruskin</persName> and his <persName corresp="#JJR">father</persName> may originally have conceived the genre \r\n                        of the work as a travelogue solely in verse&#x2014;a version represented by a manuscript now bound in <ref type="apparatus" target="msia_apparatus.php">MS IA</ref>, \r\n                        and designated in <hi rend="italic">ERM</hi> as \r\n                        <ref type="witness" target="account_of_a_tour_on_the_continent_msia_g1.php">MS IA, <hi rend="ital">g</hi>.1</ref>. \r\n                        The work returned to this characterization as an all&#x2010;verse travelogue, when selections were excerpted and revised under the title, \r\n                        <ref type="witness" target="account_of_a_tour_on_the_continent_fo.php">&#x201C;Fragments from a Metrical Journal&#x201D;</ref> \r\n                        (see <ref type="internal" target="TITLE">Title</ref>). By this time, however, the scenario of a \r\n                        &#x201C;Metrical Journal&#x201D; was imaginary, since <persName corresp="#JR">Ruskin</persName> had developed the work into a composite \r\n                        genre that included prose and graphic elements, as well as poetry, in its <ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">MS IX version</ref>.</p>\r\n                </div>\r\n                <div type="section" xml:id="MAJORMS">\r\n                    <head type="section">Manuscripts</head>\r\n                    <p><ref type="apparatus" target="msia_apparatus.php">MS IA</ref>, \r\n                        <ref type="apparatus" target="msvii_apparatus.php">MS VII</ref>, \r\n                        <ref type="apparatus" target="msviii_apparatus.php">MS VIII</ref>, \r\n                        <ref type="apparatus" target="msix_apparatus.php">MS IX</ref>, \r\n                        <ref type="apparatus" target="msxi_apparatus.php">MS XI</ref>.</p>\r\n                    <p>Facsimile and transcript by permission of Beinecke Rare Book and Manuscript Library, Yale University.</p>\r\n                    <p><ref type="apparatus" target="msia_apparatus.php">MS IA</ref> and <ref type="apparatus" target="msviii_apparatus.php">MS VIII</ref> contain draft \r\n                        of verse and prose sections for the &#x201C;Account,&#x201D; and <ref type="apparatus" target="msix_apparatus.php">MS IX</ref> contains \r\n                        <persName corresp="#JR">Ruskin</persName>&#x02BC;s fair copy of the composite genre version. \r\n                        <ref type="apparatus" target="msvii_apparatus.php">MS VII</ref> and <ref type="apparatus" target="msxi_apparatus.php">MS XI</ref> include fair copy \r\n                        of a few sections in hands other than <persName corresp="#JR">Ruskin</persName>&#x02BC;s.</p><!-- DCH: SPECIFY -->\r\n                    <p><ref type="apparatus" target="msia_apparatus.php">MS IA</ref> contains two distinct manuscripts related to the &#x201C;Account,&#x201D; \r\n                        designated in <hi rend="italic">ERM</hi> as \r\n                        <ref type="witness" target="account_of_a_tour_on_the_continent_msia_g1.php">MS IA, <hi rend="ital">g</hi>.1</ref>, and \r\n                        <ref type="witness" target="account_of_a_tour_on_the_continent_msia_g2.php">MS IA, <hi rend="ital">g</hi>.2</ref>, respectively. \r\n                        When <persName corresp="#WGC">W. G. Collingwood</persName> was preparing \r\n                        <ref type="note" subtype="bibliographical" target="poems_1891_note.php"><hi rend="italic">Poems</hi> (1891)</ref>, \r\n                        he was apparently aware of both of these manuscripts, although they had not yet been bound in <ref type="apparatus" target="msia_apparatus.php">MS IA</ref>. \r\n                        Among manuscripts currently extant, <ref type="witness" target="account_of_a_tour_on_the_continent_msia_g2.php">MS IA, <hi rend="ital">g</hi>.2</ref>, \r\n                        is the only known source of two poems, <ref type="witness" target="passing_the_alps_poem_msia_g2.php">&#x201C;Passing the Alps&#x201D;</ref> \r\n                        and <ref type="witness" target="milan_cathedral_poem_msia_g2.php">&#x201C;Milan Cathedral&#x201D;</ref>, and <persName corresp="#WGC">Collingwood</persName> \r\n                        includes both of these poems in his version of the &#x201C;Account.&#x201D; \r\n                        His aquaintance with <ref type="witness" target="account_of_a_tour_on_the_continent_msia_g1.php">MS IA, <hi rend="ital">g</hi>.1</ref> is equally probable, \r\n                        since he draws on what he calls the &#x201C;original&#x201D; draft of the poem, &#x201C;Calais,&#x201D; \r\n                        selecting lines that exist only in this version (see <ref type="gloss" target="calais_poem_glosses#CALAISPOEMLINE2">&#x201C;Calais&#x201D; [poem], gloss to line 2</ref>).</p>\r\n                    <p><persName corresp="#WGC">W. G. Collingwood</persName> is inconsistent and seemingly arbitrary in \r\n                        his reliance on these &#x201C;originals,&#x201D; however. \r\n                        When printing what he calls the &#x201C;original draft&#x201D; of the poem &#x201C;Andernacht&#x201D; and the &#x201C;first draft&#x201D; of the poem &#x201C;St. Goar,&#x201D; \r\n                        his choice among variants shows that he adopted copytext from \r\n                        <persName corresp="#JR">Ruskin</persName>&#x02BC;s fair copies in <ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">MS IX</ref>&#x2014;and \r\n                        not from these poems&#x02BC; earliest extant drafts, found in \r\n                        <ref type="witness" target="account_of_a_tour_on_the_continent_msia_g2.php">MS IA, <hi rend="ital">g</hi>.2</ref> \r\n                        (<ref type="witness" target="andernacht_poem_msia.php">&#x201C;Andernacht&#x201D;</ref> and \r\n                        <ref type="witness" target="st_goar_poem_msia.php">&#x201C;St. Goar&#x201D;</ref>; \r\n                        see <ref type="bibliography" target="bibliography.php#POEMS4D1891"><hi rend="italic">Poems</hi> (4<hi rend="superscript">o</hi>, 1891)</ref>, 1: 280&#x2013;83 n. 28, n. 30, and n. 32, and \r\n                        <ref type="bibliography" target="bibliography.php#POEMS8D1891"><hi rend="italic">Poems</hi> (8<hi rend="superscript">o</hi>, 1891)</ref>, 1: 282 n. 28, 283&#x2013;85 n. 30 and n. 32). \r\n                        Likewise inconsistent in his approach to <ref type="witness" target="account_of_a_tour_on_the_continent_msia_g1.php">MS IA, <hi rend="ital">g</hi>.1</ref>, \r\n                        he chooses arbitrarily between lines of <ref type="witness" target="calais_poem_msia_g1.php">&#x201C;Calais&#x201D;</ref> as originally composed in the hand of <persName corresp="#JJR">John James Ruskin</persName> \r\n                        and revisions in the hand of <persName corresp="#JR">John Ruskin</persName>&#x2014;and at one point, he even invents \r\n                        a line of his own rather than accept either authority.</p>\r\n                    <p>The miscellaneous contents of <ref type="apparatus" target="msia_apparatus.php">MS IA</ref> were collected and bound \r\n                        under the auspices of the editors of the \r\n                        <ref type="note" subtype="bibliographical" target="library_edition_note.php">Library Edition</ref>, \r\n                        after <persName corresp="#WGC">Collingwood</persName> published \r\n                        <ref type="note" subtype="bibliographical" target="poems_1891_note.php"><hi rend="italic">Poems</hi> (1891)</ref> \r\n                        (see <ref type="apparatus" target="msia_apparatus.php#PROVENANCE">MS IA: Provenance</ref>). \r\n                        <persName corresp="#ETC">Cook</persName> and <persName corresp="#AW">Wedderburn</persName> made greater use of these earlier versions of the "Account" than did <persName corresp="#WGC">Collingwood</persName>. \r\n                        In their editing of <ref type="witness" target="calais_poem_msia_g1.php">&#x201C;Calais&#x201D;</ref>, they \r\n                        refer to <persName corresp="#JR">Ruskin</persName>&#x02BC;s &#x201C;father&#x02BC;s copy&#x201D; \r\n                        of this poem, ascribing the text to <ref type="witness" target="account_of_a_tour_on_the_continent_msia_g1.php">MS IA, <hi rend="ital">g</hi>.1</ref>; \r\n                        and they ascribe their copytext of <ref type="witness" target="st_goar_poem_msia.php">&#x201C;St. Goar&#x201D;</ref> \r\n                        to the manuscript <ref type="witness" target="account_of_a_tour_on_the_continent_msia_g2.php">MS IA, <hi rend="ital">g</hi>.2</ref>. \r\n                        This suggests, incidentally, that \r\n                        <ref type="apparatus" target="msia_apparatus.php">MS IA</ref> had been bound by 1903.</p>\r\n                </div>\r\n                <div type="section" xml:id="DATE">\r\n                    <head type="section" >Date</head>\r\n                    <div type="subsection" xml:id="DATEOFCOMPOSITION">\r\n                        <head type="subsection">Date of Composition</head>\r\n                        <p>None of the manuscripts is dated by <persName corresp="#JR">Ruskin</persName>. Presumably, \r\n                            he began the work no earlier than the family&#x02BC;s departure for the <geogName corresp="#CONTINENT">Continent</geogName> shortly after \r\n                            <persName corresp="#JJR">John James Ruskin</persName>&#x02BC;s \r\n                            <date notBefore="1833-05-10" notAfter="1833-05-31">10 May 1833</date> \r\n                            birthday, and it is possible that he began it no earlier than following \r\n                            their return home in <date notBefore="1833-09-20" notAfter="1833-09-30">late September 1833</date>. \r\n                            Neither of these possible termini a quo can be ruled out by the physical evidence of what is likely the earliest extant manuscript, \r\n                            <ref type="witness" target="account_of_a_tour_on_the_continent_msia_g1.php">MS IA, <hi rend="ital">g</hi>.1</ref>. \r\n                            This manuscript, which is in the hands of both father and son, \r\n                            could even have been begun during the journey itself (see <ref type="internal" target="COMPPUB">Composition and Publication</ref>).</p>\r\n                        <p>If one argues a terminus a quo for <ref type="witness" target="account_of_a_tour_on_the_continent_msia_g1.php">MS IA, <hi rend="ital">g</hi>.1</ref>, \r\n                            as the return home to <placeName corresp="#HERNE">Herne Hill</placeName>, that date would be after \r\n                            <date notBefore="1833-09-19">19 September 1833</date>, which was the day that the\r\n                            family reached <placeName corresp="#BOULOGNE">Boulogne</placeName>,\r\n                            according to <persName corresp="#JJR">John James</persName>&#x02BC;s travel\r\n                            diary (<ref type="bibliography" target="bibliography.php#RFMS33A">Diary of John James Ruskin, 1833&#x2013;46</ref>, p. 74). \r\n                            A few more days would have been required in order to transact \r\n                            business and cross the <geogName corresp="#ENGLISHCHANNEL">Channel</geogName> at \r\n                            <placeName corresp="#CALAIS">Calais</placeName>.</p>\r\n                        <p>It is likely that <persName corresp="#JR">Ruskin</persName> carried out \r\n                            the bulk of the composition and fair copying (especially the composite \r\n                            genre version) between <date when="1833-10">October 1833</date> and \r\n                            <date when="1834-08">August 1834</date>, the latest date when he could have delivered his contribution, \r\n                            <ref type="witness" target="account_of_a_tour_on_the_continent_fo.php">&#x201C;Fragments from a Metrical Journal&#x201D;</ref>, along with a separate poem, \r\n                            <ref type="apparatus" target="saltzburg_apparatus.php">Saltzburg</ref> (previously misdated as <date when="1835-12">December 1835</date>), \r\n                            to <ref type="note" subtype="biographical" target="pringle_thomas_note.php">Thomas Pringle (1789&#x2013;1834)</ref> the editor of \r\n                            <ref type="bibliography" target="bibliography.php#FRIENDSHIPSOFFERING1835"><hi rend="italic">Friendship&#x02BC;s Offering; and Winter&#x02BC;s Wreath . . . for MDCCCXXXV</hi></ref> \r\n                            for publication (see <ref type="internal" target="COMPPUB">Composition and Publication</ref>). \r\n                            It is possible and even likely that, in order to devote his attention to these exciting commissions, \r\n                            <persName corresp="#JR">Ruskin</persName> ceased steady work on his \r\n                            <ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">MS IX fair copy</ref> of the &#x201C;Account&#x201D; \r\n                            some months earlier than the <date when="1834-08">August</date> deadline&#x2014;perhaps abandoning the fair copy in \r\n                            approximately <date when="1834-05">May</date>.</p>\r\n                        <p>Strong evidence in <ref type="apparatus" target="msviii_apparatus.php">MS VIII</ref> shows that \r\n                            <persName corresp="#JR">Ruskin</persName> labored on both draft and fair copy of the &#x201C;Account&#x201D; in <date when="1834">1834</date>, \r\n                            through the period when he normally prepared anniversary poetry for his father&#x02BC;s May birthday&#x2014;the poems, namely, \r\n                            <ref type="apparatus" target="the_address_apparatus.php">&#x201C;The Address&#x201D;</ref> and \r\n                            <ref type="apparatus" target="the_vintage_apparatus.php">&#x201C;The Vintage&#x201D;</ref>, along with another poem, \r\n                            <ref type="apparatus" target="the_crystal_hunter_apparatus.php">&#x201C;The Crystal Hunter&#x201D;</ref>, which he mentions in a \r\n                            <date when="1834-02-22">22 February 1834</date> letter to his father as having been &#x201C;brought to a standstill, else should be sent&#x201D; \r\n                            (<ref type="bibliography" target="bibliography#RFL">Burd, ed., <hi rend="italic">Ruskin Family Letters</hi></ref>, 280; and see \r\n                            <ref type="apparatus" target="msviii_apparatus.php#LISTB2">MS VIII: Contents, <hi rend="italic">b</hi>.2</ref>). \r\n                            Some very tenuous evidence exists that he extended work on the project until much later into the year, past <date when="1834-08">September 1834</date>: \r\n                            in the prose section of <ref type="witness" target="brussels_prose_msix">&#x201C;Brussels</ref>&#x201D;, <persName corresp="#JR">Ruskin</persName>&#x02BC;s \r\n                            description of a distant view of the city resembles features in an engraved view of the subject \r\n                            based on a drawing by <persName corresp="#JMWT">Turner</persName>, an engraving that <persName corresp="#JR">Ruskin</persName> could not have seen prior to its publication \r\n                            in <date when="1834-09">September 1834</date> (see the gloss on <persName corresp="#JR">Ruskin</persName>&#x02BC;s description of the cathedral towers in \r\n                            <ref type="witness" target="brussels_prose_part1_msviii.php">&#x201C;Part of Brussels&#x201D;</ref>). \r\n                            However, the similarity of <persName corresp="#JR">Ruskin</persName>&#x02BC;s \r\n                            and <persName corresp="#JMWT">Turner</persName>&#x02BC;s prospects could be coincidental, \r\n                            whereas the evidence for dating the draft of the prose section of <ref type="witness" target="brussels_prose_msix">&#x201C;Brussels</ref>&#x201D;, \r\n                            which is included in <ref type="witness" target="account_of_a_tour_on_the_continent_msviii.php">MS VIII</ref>, \r\n                            weighs heavily in favor of a date between <date when="1834-02">February</date> through <date when="1834-05">May 1834</date>.</p>\r\n                        <p><persName corresp="#JR">Ruskin</persName> probably started this <ref type="witness" target="account_of_a_tour_on_the_continent_msviii.php">MS VIII draft</ref> \r\n                            for the &#x201C;Account&#x201D;&#x2014;draft consisting largely of poems relating to the Alpine and Italian stages of the tour, \r\n                            along with prose relating to the Belgian and German stages&#x2014;around the time of his <date when="1834-02-08">8 February 1834 birthday</date>.  \r\n                            This important stint of draft can therefore be dated approximately between \r\n                            <date when="1834-02-08">8 February</date> and <date when="1834-05-10">10 May</date>. \r\n                            The stint terminates with draft of <persName corresp="#JR">Ruskin</persName>&#x02BC;s ekphrastic and topographical poem, \r\n                            <ref type="apparatus" target="saltzburg_apparatus.php">&#x201C;Saltzburg&#x201D;</ref>, which was commissioned for \r\n                            <ref type="bibliography" target="bibliography.php#FRIENDSHIPSOFFERING1835"><hi rend="italic">Friendship&#x02BC;s Offering; and Winter&#x02BC;s Wreath . . . for MDCCCXXXV</hi></ref>. \r\n                            According to <persName corresp="#WGC">W. G. Collingwood</persName>, the latter poem dates from <date when="1835">1835</date>, \r\n                            so that <persName corresp="#JR">Ruskin</persName> must have inserted its draft later on a blank page following the <date when="1834">1834</date> sequence; \r\n                            however, in <ref type="internal" target="COMPPUB">Composition and Publication</ref>, it is argued that the draft \r\n                            of <ref type="apparatus" target="saltzburg_apparatus.php">&#x201C;Saltzburg&#x201D;</ref> \r\n                            falls in a logical place in <ref type="apparatus" target="msviii_apparatus.php">MS VIII</ref>, as belonging to <date when="1834">1834</date>. \r\n                            Its placement at the point where the  \r\n                            <ref type="witness" target="account_of_a_tour_on_the_continent_msviii.php">MS VIII &#x201C;Account&#x201D; draft</ref> abruptly ends suggests that the commission of \r\n                            <ref type="apparatus" target="saltzburg_apparatus.php">&#x201C;Saltzburg&#x201D;</ref> \r\n                            for <hi rend="italic">Friendship&#x02BC;s Offering</hi> distracted \r\n                            <persName corresp="#JR">Ruskin</persName> from the &#x201C;Account,&#x201D; and he probably never returned to the project with sustained energy. \r\n                            No available evidence explains the timing of this commission relative to that for <ref type="witness" target="account_of_a_tour_on_the_continent_fo.php">&#x201C;Fragments from a Metrical Journal&#x201D;</ref> in the same volume \r\n                            of <hi rend="italic">Friendship&#x02BC;s Offering</hi>.</p>\r\n                        <p>A benchmark often connected with <persName corresp="#JR">Ruskin</persName>&#x02BC;s composition of the &#x201C;Account&#x201D; \r\n                            is his acquisition of the <date when="1830">1830</date> illustrated edition of the topographical poem \r\n                            <hi rend="italic">Italy</hi> by <ref type="note" subtype="biographical" target="samuel_rogers_note.php">Samuel Rogers (1763&#x2013;1855)</ref>. \r\n                            According to his story in the autobiography, <hi rend="italic">Praeterita</hi>, it was the gift of this book for <persName corresp="#JR">Ruskin</persName>&#x02BC;s \r\n                            <date when="1832-02-08">1832 birthday</date> along with the family&#x02BC;s acquisition by subscription in \r\n                            <date when="1833-04">April 1833</date> of <hi rend="italic">Facsimiles of Sketches Made in <placeName corresp="#FLANDERS">Flanders</placeName> and <placeName corresp="#GERMANY">Germany</placeName></hi> by \r\n                            <ref type="note" subtype="biographical" target="prout_samuel_note.php"><persName corresp="#SP">Samuel Prout (<date when="1783">1783</date>&#x2013;<date when="1852">1852</date>)</persName></ref> \r\n                            that prompted the <ref type="note" subtype="contextual" target="tour_of_1833_note.php">Tour of 1833</ref> \r\n                            (<ref type="bibliography" target="bibliography.php#JRWORKSLE">Ruskin, <hi rend="italic">Works</hi></ref>, 35: 79; and see \r\n                            <ref type="internal" target="#DISCUSSION">Discussion</ref>). \r\n                            The Ruskins did bring home <persName corresp="#SP">Prout</persName>&#x02BC;s book in <date when="1833-04">April 1833</date>, but it is absurd to imagine that it \r\n                            sponsored <persName corresp="#MR">Margaret</persName>&#x02BC;s suggestion that the family &#x201C;should . . . go and see some of [the wonderful places] in reality,&#x201D; \r\n                            and that forthwith &#x201C;there were two or three weeks of entirely rapturous and amazed preparation&#x201D; for such a journey. \r\n                            As <persName corresp="#JDHUNT">John Dixon Hunt</persName> points out, a letter by <persName corresp="#JR">Ruskin</persName> \r\n                            from <date when="1831-03-21">21 March 1831</date> indicates that the Ruskins had been planning their \r\n                            &#x201C;Switzerlandish outlandish tour to <placeName corresp="#ITALY">Italy</placeName>&#x201D; for at least two years \r\n                            (<ref type="bibliography" target="bibliography.php#JRWORKSLE">Ruskin, <hi rend="italic">Works</hi></ref>, 35: 79; \r\n                            <ref type="bibliography" target="bibliography#RFL">Burd, ed., <hi rend="italic">Ruskin Family Letters</hi></ref>, 286 n. 1, 253; and \r\n                            <ref type="bibliography" target="bibliography.php#WIDERSEA">Hunt, <hi rend="italic">Wider Sea</hi></ref><!-- PAGE NO. NEEDED -->).</p>\r\n                        <p>The more elusive date is when the Ruskins acquired the <date when="1830">1830</date> edition of \r\n                            <hi rend="italic">Italy</hi>. The question is of some interest, since it is argued in <ref type="internal" target="#COMPPUB">Composition and Publication</ref> \r\n                            that <persName corresp="#JR">Ruskin</persName>&#x02BC;s &#x201C;Account&#x201D; underwent a development similar to that of \r\n                            <persName corresp="#SR">Rogers</persName>&#x02BC;s <hi rend="italic">Italy</hi>&#x2014;evolving from an all&#x2010;verse to a composite&#x2010;genre work. \r\n                            Unfortunately, the evidence is too contradictory to be able to align the family&#x02BC;s acquisition of <persName corresp="#SR">Rogers</persName>&#x02BC;s \r\n                            <date when="1830">1830</date> illustrated edition with \r\n                            <persName corresp="#JR">Ruskin</persName>&#x02BC;s reworking of the &#x201C;Account.&#x201D; In the autobiography, \r\n                            <persName corresp="#JR">Ruskin</persName> himself admits that he has \r\n                            &#x201C;told this story so often&#x201D; about the influence of <persName corresp="#SR">Roger</persName>&#x02BC;s illustrated edition \r\n                            and its revelation of <persName corresp="#JMWT">Turner</persName>&#x02BC;s art, &#x201C;that I begin to doubt its time.&#x201D; \r\n                            In <hi rend="italic">Praeterita</hi>&#x02BC;s typically dramatizing fashion, <persName corresp="#JR">Ruskin</persName> declares that \r\n                            &#x201C;the main tenor of my life&#x201D; was set on <date when="1832-02-08">8 February 1832</date> by the birthday gift from his father&#x02BC;s business partner, \r\n                            <ref type="note" subtype="biographical" target="telford_henry_note.php">Henry Telford (d. 1859)</ref>. \r\n                            &#x201C;It is curiously tiresome that Mr. <persName corresp="#HT">Telford</persName> did not himself write my name in the book, \r\n                            and my father, who writes in it, &#x2018;The gift of <persName corresp="#HT">Henry Telford</persName>, Esq.,&#x2019;  \r\n                            still more curiously, for him, puts no date: if it was a year later [i.e., <date when="1833-02-08">8 February 1833</date>], no matter&#x201D; \r\n                            (<ref type="bibliography" target="bibliography.php#JRWORKSLE">Ruskin, <hi rend="italic">Works</hi></ref>, 35: 79). \r\n                            In fact, the possible range is even wider. \r\n                            In a non&#x2010;illustrated edition, <persName corresp="#SR">Rogers</persName>&#x02BC;s poem had been in the <persName corresp="#JR">Ruskin</persName> household \r\n                            since <date when="1828">1828</date> at the latest \r\n                            (<ref type="bibliography" target="bibliography#RFL">Burd, ed., <hi rend="italic">Ruskin Family Letters</hi></ref>, 188 n. 4). \r\n                            This happens to be the year when <persName corresp="#SR">Rogers</persName> published &#x201C;Part the Second&#x201D; of the poem; \r\n                            and growing disgusted over the work&#x02BC;s tepid reception, he bought out remaining copies of both that volume and &#x201C;Part the First,&#x201D; and destroyed them \r\n                            (<ref type="bibliography" target="bibliography.php#ROGERSITALY1828">Rogers, <hi rend="italic">Italy</hi> (1828)</ref>; \r\n                            <ref type="bibliography" target="bibliography.php#ROGERSITALY1823">Rogers, <hi rend="italic">Italy</hi> (1823)</ref>; \r\n                            and see <ref type="note" subtype="biographical" target="samuel_rogers_note.php">Samuel Rogers [1763&#x2013;1855]</ref>). \r\n                            Thus, even before <persName corresp="#SR">Rogers</persName>&#x02BC;s poem achieved popularity in the illustrated edition, \r\n                            the Ruskins could count themselves among the select number of appreciative readers of the work in its original form, \r\n                            and it is possible that <persName corresp="#JR">Ruskin</persName> and <persName corresp="#JJR">his father</persName> \r\n                            projected a work on this unillustrated verse model prior to <persName corresp="#JR">Ruskin</persName>&#x02BC;s receiving the gift of the illustrated version.</p>\r\n                        <p>When, then, did <persName corresp="#JR">Ruskin</persName> first see the <date when="1830">1830</date> version? \r\n                            It is possible that the gift did spur the family&#x02BC;s travel plans, but earlier than <persName corresp="#JR">Ruskin</persName> remembered, \r\n                            since the first mention of Continental tour occurs in the letters the family letters for <date when="1831">1831</date>. \r\n                            His <date when="1831-02-08">1831 birthday</date> predates that first mention by only a month. \r\n                            An alternative, surprising possibility is that <persName corresp="#JR">Ruskin</persName> owned the illustrated edition only after \r\n                            the family had returned from the <geogName corresp="#CONTINENT">Continent</geogName>. According to <persName corresp="#JJR">John James</persName>&#x02BC;s accounts, \r\n                            he purchased a copy of <hi rend="italic">Italy</hi> \r\n                            in <date when="1833-10">October 1833</date>, just shortly after the <date when="1833-09">September</date> homecoming \r\n                            (<ref type="bibliography" target="bibliography#RFL">Burd, ed., <hi rend="italic">Ruskin Family Letters</hi></ref>, 286 n. 1). \r\n                            Could this explain why <persName corresp="#HT">Telford</persName>&#x02BC;s dedication is in <persName corresp="#JJR">John James</persName>&#x02BC;s hand? \r\n                            This <date when="1833-10">October 1833</date> purchase could well have been an additional copy of the <date when="1830">1830</date> version \r\n                            that was already well loved by the family, \r\n                            and it seems unlikely that the Ruskins would have acquired this popular book so belatedly. Nonetheless, \r\n                            one cannot discount the possibility that <persName corresp="#SR">Rogers</persName>&#x02BC;s tutelage of the Ruskins&#x02BC; \r\n                            viewing of the <placeName corresp="#ALPS">Alps</placeName> and <placeName corresp="#ITALY">Italy</placeName> was, during the tour itself, solely poetic \r\n                            and based on earlier versions of the poem, \r\n                            whereas the ekphrastic influence of <persName corresp="#JMWT">Turner</persName> and <persName corresp="#TSP">Stothard</persName> \r\n                            enhanced <persName corresp="#JR">Ruskin</persName>&#x02BC;s perception at almost precisely the time when he developed an illustrated version of his own.</p>\r\n                    </div>\r\n                    <div type="subsection" xml:id="DATEOFPUBLICATION">\r\n                            <head type="subsection">Date of Publication</head>\r\n                        <p><persName corresp="#WGC">W. G. Collingwood</persName> attributed the first published version of the &#x201C;Account,&#x201D; \r\n                            <ref type="witness" target="account_of_a_tour_on_the_continent_fo.php">&#x201C;Fragments from a Metrical Journal&#x201D;</ref>, to <date when="1835">1835</date>, \r\n                            based on its appearance in <ref type="bibliography" target="bibliography.php#FRIENDSHIPSOFFERING1835"><hi rend="italic">Friendship&#x02BC;s Offering; and Winter&#x02BC;s Wreath . . . for MDCCCXXXV</hi></ref>. \r\n                            Forgetting that <ref type="note" subtype="bibliographical" target="annuals_note.php">annuals</ref> \r\n                            were always dated on their title pages for the year following the actual date of publication, \r\n                            because they were produced for gift&#x2010;giving during the holiday season of Christmas and New Year&#x02BC;s, \r\n                            <persName corresp="#WGC">Collingwood</persName> was additionally befuddled by his assumption that another poem by <persName corresp="#JR">Ruskin</persName> \r\n                            in the same volume of <hi rend="italic">Friendship&#x02BC;s Offering</hi>, \r\n                            <ref type="apparatus" target="saltzburg_apparatus.php">&#x201C;Saltzburg&#x201D;</ref>, could have dated only from the Ruskin family&#x02BC;s visit \r\n                            to <placeName corresp="SALZBURG">Salzburg</placeName> during their Continental \r\n                            <ref type="note" subtype="contextual" target="tour_of_1835_note.php">Tour of 1835</ref>. \r\n                            <persName corresp="#JR">Ruskin</persName>&#x02BC;s poem was based, however, not on first&#x2010;hand experience \r\n                            but on an engraved vedute commissioned for the &#x201C;<date when="1835">1835</date>&#x201D; volume of <hi rend="italic">Friendship&#x02BC;s Offering</hi> \r\n                            that was released for sale in <date when="1834-11">November 1834</date>. See \r\n                            <ref type="bibliography" target="bibliography.php#WGCLIFEOFJR1893">Collingwood, <hi rend="italic">Life and Work of John Ruskin</hi></ref>, 1:59&#x2013;60; \r\n                            <ref type="bibliography" target="bibliography.php#POEMS4D1891"><hi rend="italic">Poems</hi> (4<hi rend="superscript">o</hi>, 1891)</ref>, 1: 280&#x2013;83 n. 28, n. 30, and n. 32; \r\n                            <ref type="bibliography" target="bibliography.php#POEMS8D1891"><hi rend="italic">Poems</hi> (8<hi rend="superscript">o</hi>, 1891)</ref>, 1: 282 n. 28, 283&#x2013;85 n. 30 and n. 32; \r\n                            and see <ref type="internal" target="#PUBLICATION">Publication</ref> for dates and discussion of subsequent publications of the &#x201C;Account.&#x201D;</p>\r\n                    </div>\r\n                </div>\r\n                <div type="section" xml:id="COMPPUB">\r\n                    <head type="section">Composition and Publication</head>\r\n                    <p>To reconstruct the composition and explain the publication history of the \r\n                        &#x201C;Account,&#x201D; one must cope with complex materials distributed \r\n                        throughout several distinct manuscripts. \r\n                        <persName corresp="#JR">Ruskin</persName> never completed the work, and we cannot know to what \r\n                        extent the surviving manuscripts represent the totality of versions that he \r\n                        conceived at any given stage of composition. The extant manuscripts do, however, \r\n                        appear representative of his evolving intentions. The compositional history is presented here, first, \r\n                        in the form of a summary <ref type="internal" target="#OVERVIEW">Overview</ref>; \r\n                        and then the history is elaborated in separate accounts of the <ref type="internal" target="#DRAFT">Draft</ref>,  \r\n                        <ref type="internal" target="#FAIRCOPY">Fair Copy</ref>, \r\n                        <ref type="internal" target="#PUBLICATION">Publication</ref> histories, \r\n                        and the issue of <ref type="internal" target="#LINENUMBERING">Line Numbering</ref>. The analysis is supported by two tables. \r\n                        <ref type="internal" target="APPARATUSTABLE1">Table 1</ref> lists the sequential contents of each of the manuscripts and of the published versions of the work. \r\n                        <ref type="internal" target="APPARATUSTABLE2">Table 2</ref> lists the sequential contents and planned extension of the final manuscript version.</p><!-- DCH: NEED ONLY LINK TO PROPOSED TOC? -->\r\n                    <div type="subsection" xml:id="OVERVIEW">\r\n                        <head type="subsection">Overview</head>\r\n                        <p>The evolution of the &#x201C;Account,&#x201D; culminated in edited \r\n                            publications of <date when="1834-11">November 1834</date>, <date when="1850">1850</date>, \r\n                            <date when="1891">1891</date>, and <date when="1902">1903</date>. \r\n                            In none of these did the editors make explicit the authority for their texts. In general, \r\n                            however, the successive published versions may be said steadily to approach closer to realizing \r\n                            <persName corresp="#JR">Ruskin</persName>&#x02BC;s intentions for the latest of his manuscript \r\n                            versions&#x2014;the composite&#x2010;genre <ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">MS IX fair copy</ref>, along with \r\n                            his plan, never realized, for extending and completing the work. Although earlier versions became incorporated into the \r\n                            <ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">MS IX version</ref>, that final, composite&#x2010;genre form was not necessarily implied \r\n                            in earlier versions; and those earlier versions arguably show <persName corresp="#JR">Ruskin</persName> \r\n                            pursuing a distinct form of travelogue from what the work became.</p>\r\n                        <p>Here is a summary of the stages of composition and publication, as represented by surviving manuscripts.</p>\r\n                        <list type="unordered">\r\n                            <item>In the first stage of composition, as represented by two sheets later bound in \r\n                                <ref type="apparatus" target="msia_apparatus.php">MS IA</ref>, and designated in <hi rend="italic">ERM</hi> as \r\n                                <ref type="witness" target="account_of_a_tour_on_the_continent_msia_g1.php">MS IA, <hi rend="ital">g</hi>.1</ref>, \r\n                                <persName corresp="#JR">John</persName> and <persName corresp="#JJR">John James Ruskin</persName> \r\n                                collaborated on an all&#x2010;verse version of the travelogue, relating the family&#x02BC;s initial stage of their journey, \r\n                                which took them through northern <placeName corresp="#FRANCE">France</placeName> and <placeName corresp="#BELGIUM">Belgium</placeName>. \r\n                                The manuscript contains poems entitled <ref type="witness" target="calais_poem_msia_g1.php">&#x201C;Calais&#x201D;</ref>, \r\n                                <ref type="witness" target="cassel_poem_msia_g1.php">&#x201C;Cassel&#x201D;</ref>, <ref type="witness" target="lille_poem_msia_g1.php">&#x201C;Lille&#x201D;</ref>, \r\n                                <ref type="witness" target="brussels_poem_msia_g1.php">&#x201C;Brussels&#x201D;</ref>, and <ref type="witness" target="the_meuse_poem_msia_g1.php">&#x201C;The Meuse&#x201D;</ref>. \r\n                                The father and son composed this version either during the <date notBefore="1833-05" notAfter="1833-09">May&#x2010;September 1833</date> journey itself, \r\n                                or following the family&#x02BC;s return home around <date when="1833-09-20">20 September</date> (see <ref type="internal" target="#DATE">Date</ref>).</item>\r\n                            <item>In a second stage of composition, <persName corresp="#JR">Ruskin</persName> reconceptualized and elaborated the work as a composite&#x2010;genre \r\n                                rather than as a solely verse travelogue. This stage is epitomized in brief by a folded sheet, \r\n                                <ref type="witness" target="account_of_a_tour_on_the_continent_msia_g2.php">MS IA, <hi rend="ital">g</hi>.2</ref>, \r\n                                which like <ref type="witness" target="account_of_a_tour_on_the_continent_msia_g1.php">MS IA, <hi rend="ital">g</hi>.1</ref> was later bound in \r\n                                <ref type="apparatus" target="msia_apparatus.php">MS IA</ref>. Serving perhaps as a sample or an abstract of the fully developed composite&#x2010;genre work \r\n                                to come, <ref type="witness" target="account_of_a_tour_on_the_continent_msia_g2.php">MS IA, <hi rend="ital">g</hi>.2</ref> contains a draft of the prose section, \r\n                                <ref type="witness" target="calais_prose_msia.php">&#x201C;Calais&#x201D;</ref>, along with draft of the poems,\r\n                                <ref type="witness" target="passing_the_alps_msia_g2.php">&#x201C;Passing the Alps&#x201D;</ref>, \r\n                                <ref type="witness" target="milan_cathedral_msia_g2.php">&#x201C;Milan Cathedral,&#x201D;</ref>, \r\n                                <ref type="witness" target="andernacht_poem_msia.php">&#x201C;Andernacht&#x201D;</ref>, and \r\n                                <ref type="witness" target="st_goar_poem_msia.php">&#x201C;St. Goar&#x201D;</ref>. \r\n                                <persName corresp="#JR">Ruskin</persName> continued to develop the composite&#x2010;genre version in draft that occupies a significant portion of \r\n                                <ref type="apparatus" target="msviii_apparatus.php">MS VIII</ref> \r\n                                (see <ref type="apparatus" target="msviii_apparatus.php#SECTIONB">MS VIII: Contents, section <hi rend="italic">b</hi></ref>).</item>\r\n                            <item>The <ref type="witness" target="account_of_a_tour_on_the_continent_msviii.php">MS VIII draft</ref> includes verse, prose, and some rough drawings, \r\n                                supplying text for the fair copy of the composite&#x2010;genre version that <persName corresp="#JR">Ruskin</persName> must by this time have had underway in \r\n                                <ref type="apparatus" target="msix_apparatus.php">MS IX</ref>. This draft relates mainly to the <geogName corresp="#RHINE">Rhine</geogName> journey, \r\n                                the sighting and crossing of the <geogName corresp="#ALPS">Alps</geogName>, the tour in northern <placeName corresp="#ITALY">Italy</placeName>, \r\n                                and the tour of <placeName corresp="#SWITZERLAND">Switzerland</placeName> \r\n                                (see <ref type="internal" target="APPARATUSTABLE1">Table 1</ref> for complete contents). Near the beginning of this draft appear the prose sections for \r\n                                <ref type="witness" target="andernacht_prose_msviii.php">&#x201C;Andernacht&#x201D;</ref> and \r\n                                <ref type="witness" target="st_goar_prose_msviii.php">&#x201C;St. Goar&#x201D;</ref>, suggestively dovetailing with the corresponding poems about those places drafted in \r\n                                <ref type="witness" target="account_of_a_tour_on_the_continent_msia_g2.php">MS IA, <hi rend="ital">g</hi>.2</ref>. \r\n                                Thus, when <persName corresp="#JR">Ruskin</persName> took up <ref type="apparatus" target="msviii_apparatus.php">MS VIII</ref> \r\n                                to continue his draft (probably around the start of <date when="1834-02">February 1834</date>), \r\n                                he was catching up with composition of prose sections to match with poetry about places in <placeName corresp="#BELGIUM">Belgium</placeName> \r\n                                and <geogName corresp="#GERMANY">Germany</geogName>, which he had already composed in the <ref type="apparatus" target="msia_apparatus.php">MS IA</ref> \r\n                                &#x201C;Account&#x201D; manuscripts. \r\n                                The next prose sections to appear in the <ref type="witness" target="account_of_a_tour_on_the_continent_msviii.php">MS VIII sequence of drafts</ref>, \r\n                                following the prose for <ref type="witness" target="andernacht_prose_msviii.php">&#x201C;Andernacht&#x201D;</ref> and for <ref type="witness" target="st_goar_prose_msviii.php">&#x201C;St. Goar&#x201D;</ref>, \r\n                                are those for <ref type="witness" target="the_meuse_prose_msviii.php">&#x201C;The Meuse&#x201D;</ref>, \r\n                                <ref type="witness" target="cologne_prose_msviii.php">&#x201C;Cologne&#x201D;</ref>, and \r\n                                <ref type="witness" target="brussels_prose_msviii.php">&#x201C;Brussels&#x201D;</ref>, \r\n                                suggesting that by this point <persName corresp="#JR">Ruskin</persName> had fair&#x2010;copied the full composite sections of verse, prose, and drawing for \r\n                                &#x201C;Calais,&#x201D; &#x201C;Cassel,&#x201D; and &#x201C;Lille&#x201D; in <ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">MS IX</ref>. \r\n                                Meanwhile, mixed in with this prose draft relating to northern <placeName corresp="#EUROPE">Europe</placeName> in \r\n                                <ref type="witness" target="account_of_a_tour_on_the_continent_msviii.php">MS VIII</ref>, <persName corresp="#JR">Ruskin</persName> carried forward with poetry about the <geogName corresp="#ALPS">Alps</geogName>\r\n                                (see <ref type="apparatus" target="msviii_apparatus.php#LISTB1">MS VIII: Contents, section <hi rend="italic">b</hi>, list <hi rend="italic">b</hi>.1</ref>).</item>\r\n                            <item>In <ref type="apparatus" target="msix_apparatus.php">MS IX</ref>, containing the elegant and elaborate \r\n                                <ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">fair copy</ref> of the composite&#x2010;genre version of the &#x201C;Account,&#x201D; \r\n                                <persName corresp="#JR">Ruskin</persName> mimicked the verse travelogues and prose landscape \r\n                                <ref type="note" subtype="bibliographical" target="annuals_note.php">annuals</ref> of the <date notBefore="1830" notAfter="1839">1830s</date>, \r\n                                which featured steel engravings after drawings by major landscape artists, and letterpress in verse or prose by writers \r\n                                who specialized in on&#x2010;demand travel writing, regardless of whether these writers had personally visited the places they described \r\n                                (see <ref type="internal" target="#DISCUSSION">Discussion</ref>). \r\n                                <persName corresp="#JR">Ruskin</persName> organized the fair copy as composite groups, \r\n                                each typically consisting of a topographical poem about the place, a prose account of travel adventures, and related drawings, \r\n                                and each entitled according to major destinations along the course of the Ruskins&#x02BC; journey. \r\n                                In the drawings, <persName corresp="#JR">Ruskin</persName> either exactingly copied or imitated and adapted the manner of work by topographical and figure illustrators, \r\n                                which he knew from publications of the <date notBefore="1830" notAfter="1839">1830s</date>. His models included, most prominently, landscape vignettes by \r\n                                <ref type="note" subtype="biographical" target="turner_j_m_w_note.php">J. M. W. Turner (1775&#x2013;1851)</ref> and figure vigettes by \r\n                                <ref type="note" subtype="biographical" target="stothard_thomas_note.php">Thomas Stothard (1755&#x2013;1834)</ref> for \r\n                                the <date when="1830">1830</date> illustrated edition of <hi rend="italic">Italy</hi> by \r\n                                <ref type="note" subtype="biographical" target="samuel_rogers_note.php">Samuel Rogers (1763&#x2013;1855)</ref>; \r\n                                and the lithographs of architectural scenes \r\n                                by <ref type="note" subtype="biographical" target="prout_samuel_note.php">Samuel Prout (1783&#x2013;1852)</ref> \r\n                                for the <date when="1833">1833</date> <hi rend="italic">Facsimiles of Sketches Made in Flanders and Germany</hi>. \r\n                                But <persName corresp="#JR">Ruskin</persName> drew on other graphic sources, as well, or invented his own scenes.</item>\r\n                            <item><persName corresp="#JR">Ruskin</persName> failed to complete the <ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">MS IX fair copy</ref>, \r\n                                but he outlined its planned extension in a \r\n                                <ref type="witness" target="account_of_a_tour_on_the_continent_msviii_contents.php">List of Proposed Additional Contents for Account of a Tour on the Continent</ref>, \r\n                                which he outlined on the back endpapers of <ref type="apparatus" target="msviii_apparatus.php">MS VIII</ref>. \r\n                                Several of these proposed additions are represented by poetry or prose \r\n                                sections that he had already drafted in <ref type="witness" target="account_of_a_tour_on_the_continent_msviii.php">MS VIII</ref>, \r\n                                and some illustrations proposed in the list may correspond to drawings that form a brief gallery of Alpine scenes \r\n                                pasted onto otherwise blank pages at the end of the <ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">MS IX fair copy</ref>.<!-- DCH: The gallery needs a name. What kind of file is it? --> \r\n                                Draft writing and drawings for this proposed extension of the fair copy may have been lost, but much of the ambitious plan probably was never executed. \r\n                                A few other fugitive pieces of writing, probably intended for the &#x201C;Account&#x201D; and fair&#x2010;copied in hands other than <persName corresp="#JR">Ruskin</persName>&#x02BC;s, \r\n                                can be found in <ref type="apparatus" target="msvii_apparatus.php">MS VII</ref> and \r\n                                <ref type="apparatus" target="msxi_apparatus.php">MS XI</ref>.</item>\r\n                            <item><persName corresp="#JR">Ruskin</persName> commenced his <ref type="witness" target="account_of_a_tour_on_the_continent_msviii_contents.php">List of Proposed Additional Contents for Account of a Tour on the Continent</ref> \r\n                                with entries for <placeName corresp="#ANDERNACHT">Andernacht</placeName>, <placeName corresp="#EHRENBREITSTEIN">Ehrenbreitstein</placeName>, <placeName corresp="#STGOAR">St. Goar</placeName>, \r\n                                <placeName corresp="#HEIDELBERG">Heidelberg</placeName>, and <placeName corresp="#SCHAFFHAUSEN">Schaffhausen</placeName>&#x2014;sections \r\n                                for which he had drafted&#x2014;or was in process of drafting&#x2014;poems and prose in <ref type="witness" target="account_of_a_tour_on_the_continent_msviii.php">MS VIII</ref>. \r\n                                Since this draft occurs shortly prior to and overlaps with draft of poetry for his <persName corresp="#JJR">father</persName>&#x02BC;s <date when="1834-05-10">10 May 1834</date> birthday, \r\n                                <persName corresp="#JR">Ruskin</persName> perhaps began compiling his list of proposed contents in <date notBefore="1834-03-01" notAfter="1834-04-30">March or April 1834</date>. \r\n                                The fair copy in <ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">MS IX</ref> breaks off in the middle of the section on \r\n                                <placeName corresp="#HEIDELBERG">Heidelberg</placeName>, suggesting that <persName corresp="#JR">Ruskin</persName> ceased fair&#x2010;copying \r\n                                at about the same time that he stopped drafting in <ref type="witness" target="account_of_a_tour_on_the_continent_msviii.php">MS VIII</ref> and developed the projected list of contents. \r\n                                However, as indicated by a list for projected development of the project, \r\n                                <persName corresp="#JR">Ruskin</persName> did not so much abandon the &#x201C;Account&#x201D; \r\n                                as put the work on hold&#x2014;and the reason was not the distraction of a family tour following his \r\n                                <persName corresp="#JJR">father</persName>&#x02BC;s birthday, but a new major project. <persName corresp="#JR">Ruskin</persName> segued his achievement \r\n                                in the &#x201C;Account&#x201D; into an exciting commmission for the engraved gift annual, \r\n                                <ref type="note" subtype="bibliographical" target="friendships_offering_note.php"><hi rend="italic">Friendship&#x02BC;s Offering</hi></ref>. \r\n                                Drawing directly on the text of the &#x201C;Account,&#x201D; he revised the poems \r\n                                <ref type="witness" target="andernacht_poem_fo.php">&#x201C;Andernacht&#x201D;</ref> and \r\n                                <ref type="witness" target="st_goar_poem_fo.php">&#x201C;St. Goar&#x201D;</ref>, which were \r\n                                published under the collected title, <ref type="witness" target="account_of_a_tour_on_the_continent_fo.php">&#x201C;Fragments from a Metrical Journal&#x201D;</ref>. \r\n                                Even more auspiciously, for the same volume of <hi rend="italic">Friendship&#x02BC;s Offering</hi>, \r\n                                he drew on his self&#x2010;instruction in the materiality of illustrated travel publications of the \r\n                                <date notBefore="1830" notAfter="1839">1830s</date> to compose an ekphrastic, topographical poem, \r\n                                <ref type="apparatus" target="saltzburg_apparatus.php">&#x201C;Saltzburg&#x201D;</ref>, \r\n                                based on an engraving of that title. Both the picture by the artist, \r\n                                <ref type="note" subtype="biographical" target="purser_william_note.php">William Purser (ca. 1790&#x2013;1852)</ref>, and the poem presumably were commissioned by \r\n                                the editor, <ref type="note" subtype="biographical" target="pringle_thomas_note.php">Thomas Pringle (1789&#x2013;1834)</ref>, \r\n                                and were published in <ref type="bibliography" target="bibliography.php#FRIENDSHIPSOFFERING1835"><hi rend="italic">Friendship&#x02BC;s Offering; and Winter&#x02BC;s Wreath . . . for MDCCCXXXV</hi></ref>. \r\n                                <persName corresp="#JR">Ruskin</persName>&#x02BC;s new poem, as well as his revised poems for the <ref type="witness" target="account_of_a_tour_on_the_continent_fo.php">&#x201C;Fragments from a Metrical Journal&#x201D;</ref>, \r\n                                must have been completed no later than <date when="1834-08">August 1834</date>, \r\n                                when <persName corresp="#TP">Pringle</persName>&#x02BC;s compilation was due to the printer in time for publication in <date when="1834-11">November 1834</date>.</item>\r\n                            <item>In <date when="1850">1850</date>, <persName corresp="#JJR">John James Ruskin</persName> and <persName corresp="#JR">Ruskin</persName>&#x02BC;s editor, \r\n                                <ref type="note" subtype="biographical" target="harrison_w_h_note.php">W. H. Harrison</ref>, published \r\n                                <ref type="witness" target="ehrenbreitstein_poem_1850.php">Ehrenbreitstein</ref> in \r\n                                <ref type="note" subtype="bibliographical" target="poems_1850_note.php">Ruskin, <hi rend="italic">Poems</hi> (1850)</ref>. \r\n                                As one reason for selecting this poem from among the unpublished verse for &#x201C;Account,&#x201D; \r\n                                the editors may have meant to repair a rift caused by the omission of this poem from <ref type="witness" target="account_of_a_tour_on_the_continent_fo.php">&#x201C;Fragments from a Metrical Journal&#x201D;</ref>, \r\n                                since, in the <ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">MS IX fair copy</ref> of the &#x201C;Account,&#x201D; \r\n                                the poems &#x201C;Andernacht,&#x201D; &#x201C;Ehrenbreitstein,&#x201D; and &#x201C;St. Goar&#x201D; form a connected trio.</item>\r\n                            <item>Almost six decades after <persName corresp="#JR">Ruskin</persName> started the &#x201C;Account,&#x201D; \r\n                                two versions of the work were edited for publication. One version, \r\n                                prepared by <persName corresp="#WGC">W. G. Collingwood</persName> \r\n                                for <ref type="note" subtype="bibliographical" target="poems_1891_note.php"><hi rend="italic">Poems</hi> (1891)</ref>, \r\n                                consisted solely of verse extracts, in keeping with the parameters of <persName corresp="WGC">Collingwood</persName>&#x02BC;s anthology. \r\n                                A second version, edited by <persName corresp="#ETC">E. T. Cook</persName> and \r\n                                <persName corresp="#AW">Alexander Wedderburn</persName> for the second volume (<date when="1903">1903</date>) of the \r\n                                <ref type="note" subtype="bibliographical" target="library_edition_note.php">Library Edition</ref>, \r\n                                used <persName corresp="#WGC">Collingwood</persName>&#x02BC;s version as a base text, \r\n                                but folded in <persName corresp="#JR">Ruskin</persName>&#x02BC;s complementary prose sections \r\n                                from <ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">MS IX</ref> along with some additional verse \r\n                                that <persName corresp="#WGC">Collingwood</persName> had neglected in <ref type="witness" target="account_of_a_tour_on_the_continent_msviii.php">MS VIII</ref>.</item>\r\n                            <item>Both of these edited versions used the <ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">MS IX</ref> fair copy as main copytext, \r\n                                supplemented by draft from <ref type="witness" target="account_of_a_tour_on_the_continent_msviii.php">MS VIII</ref> in order to represent the full scope of the \r\n                                <date when="1833">1833</date> tour. Both versions also preferred <persName corresp="#JR">Ruskin</persName>&#x02BC;s revised texts of \r\n                                <ref type="witness" target="andernacht_poem_fo.php">&#x201C;Andernacht&#x201D;</ref> and \r\n                                <ref type="witness" target="st_goar_poem_fo.php">&#x201C;St. Goar&#x201D;</ref> \r\n                                as published in <ref type="note" subtype="bibliographical" target="friendships_offering_note.php"><hi rend="italic">Friendship&#x02BC;s Offering</hi></ref>, \r\n                                although printing those poems&#x02BC; earlier versions in the notes. \r\n                                <persName corresp="#ETC">Cook</persName> and <persName corresp="#AW">Wedderburn</persName>&#x02BC;s version was the first to reproduce a page \r\n                                from <ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">MS IX</ref> exemplifying <persName corresp="#JR">Ruskin</persName>&#x02BC;s integration of text and picture. \r\n                                <persName corresp="#WGC">Collingwood</persName>&#x02BC;s edition reproduced sketches that <persName corresp="#JR">Ruskin</persName> made during the actual tour (these also reprinted in the Library Edition version). \r\n                                Thus, the Library Edition edition version comes closest to realizing <persName corresp="#JR">Ruskin</persName>&#x02BC;s intentions \r\n                                for the composite&#x2010;genre version of the &#x201C;Account,&#x201D; considered as a reflection of illustrated travelogues of the <date notBefore="1830" notAfter="1839">1830s</date>, \r\n                                whereas <persName corresp="#WGC">Collingwood</persName>&#x02BC;s version seems constructed as a biographical souvenir of the <date when="1833">1833</date> tour. \r\n                                <persName corresp="#WGC">Collingwood</persName> did not justify his editorial procedures for the &#x201C;Account,&#x201D; and he is unlikely to have derived textual authority \r\n                                for an all&#x2010;verse version from <ref type="witness" target="account_of_a_tour_on_the_continent_msia_g1.php">MS IA, <hi rend="ital">g</hi>.1</ref>, \r\n                                which he appears not to have known (see gloss, line 2, <ref type="witness" target="calais_poem_1891.php">&#x201C;Calais&#x201D;</ref>). \r\n                                In light of <persName corresp="#JR">Ruskin</persName>&#x02BC;s dismissive remarks about the &#x201C;Account&#x201D; in chapter 4 (<date when="1885">1885</date>) of the autobiography, <hi rend="italic">Praeterita</hi>, \r\n                                it is unlikely that the now elderly <persName corresp="#JR">Ruskin</persName> contributed to <placeName corresp="#WGC">Collingwood</placeName>&#x02BC;s editing, \r\n                                except by indirectly encouraging the biographical approach that underpins <persName corresp="#WGC">Collingwood</persName>&#x02BC;s procedure&#x2014;and \r\n                                even in this respect, the editor elaborated his own story about the Ruskins&#x02BC; <date when="1833">1833</date> Continental tour, \r\n                                as presented in his <date when="1894">1894</date> biography, <ref type="bibliography" target="bibliography.php#WGCLIFEOFJR1893"><hi rend="italic">Life and Work of John Ruskin</hi></ref> \r\n                                (see <ref type="bibliography" target="bibliography.php#JRWORKSLE">Ruskin, <hi rend="italic">Works</hi></ref>, 35: 81; and see <ref type="internal" target="#DISCUSSION">Discussion</ref>).</item>\r\n                            </list>\r\n                        <p>This overview is substantiated by detailed analysis in <ref type="internal" target="#DRAFT">Draft</ref>, \r\n                            <ref type="internal" target="#PROPOSEDCONTENTS">Ruskin&#x02BC;s Plans for Extension of the &#x201C;Account&#x201D;</ref>, \r\n                            <ref type="internal" target="#FAIRCOPY">Fair Copy</ref>, and <ref type="internal" target="#PUBLICATION">Publication</ref>, \r\n                            and <ref type="internal" target="#LINENUMBERING">Line Numbering</ref>. \r\n                            For ease of reference, the analysis is supported by tables, which \r\n                            organize information about the contents of the respective manuscripts.</p>\r\n                        <list type="unordered">\r\n                            <item>In <ref type="internal" target="APPARATUSTABLE1">Table 1</ref>, \r\n                                the columns list vertically the separate verse and prose sections and illustrations of the &#x201C;Account,&#x201D; \r\n                                as they appear sequentially in the manuscripts or parts of manuscripts containing the work. \r\n                                Horizontally, the columns represent a rough chronological order, from the conjecturally earliest to the latest of the manuscripts, \r\n                                ending with the versions of the &#x201C;Account&#x201D; edited for publication; however, \r\n                                <persName corresp="#JR">Ruskin</persName> likely worked on more than one of these manuscripts simultaneously&#x2014;especially, \r\n                                on the <ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">MS IX fair copy</ref>, \r\n                                alongside the <ref type="witness" target="account_of_a_tour_on_the_continent_msviii.php">MS VIII draft</ref>&#x2014;and \r\n                                so the sequence of composition, section by section, cannot be recovered in very specific detail. \r\n                                Nonetheless, in a broad view, the table represents <persName corresp="#JR">Ruskin</persName>&#x02BC;s developing intentions. \r\n                                This table also provides information about <persName corresp="#JR">Ruskin</persName>&#x02BC;s system of line numbering, \r\n                                which he attached to the verse sections of some manuscripts, \r\n                                and which may provide clues about the sequence of composition.</item>\r\n                            <item><ref type="internal" target="APPARATUSTABLE2">Table 2</ref> organizes information about the contents of the &#x201C;Account&#x201D; \r\n                                from the perspective of <persName corresp="#JR">Ruskin</persName>&#x02BC;s final version, as he partly realized his vision in the fair copy, \r\n                                <ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">MS IX</ref>, \r\n                                and as he prospectively outlined the work&#x02BC;s planned completion in the \r\n                                <ref type="witness" target="account_of_a_tour_on_the_continent_msviii_contents_msviii.php">List of Proposed Additional Contents for Account of a Tour on the Continent</ref>. \r\n                                To the right of this first vertical column, additional columns indicate where drafts or fair copies of these respective prose or verse sections \r\n                                or illustrations can be located in surviving manuscripts or published versions. In other words, \r\n                                this table provides a checklist for the location of draft, fair copy, and publication of individual sections of the &#x201C;Account.&#x201D; \r\n                                The table makes note of editorial interventions in titles.</item>\r\n                            </list>\r\n                    </div>\r\n                    <div>\r\n                        <table rows="20" cols="9" xml:id="APPARATUSTABLE1">\r\n                            <head>Table 1. Account of a Tour on the Continent: Sequence of Texts (Verse and Prose) and Drawings within Corpuses, by Manuscript or Edition</head>\r\n                            <row role="label">\r\n                                <cell n="1" role="data"><ref type="witness" target="account_of_a_tour_on_the_continent_msia_g1.php">MS IA, <hi rend="ital">g</hi>.1</ref></cell>\r\n                                <cell n="2" role="data"><ref type="witness" target="account_of_a_tour_on_the_continent_msia_g1.php">MS IA, <hi rend="ital">g</hi>.1</ref>, \r\n                                    <persName corresp="#JR">Ruskin</persName>&#x02BC;s Line Numbering</cell>\r\n                                <cell n="3" role="data"><ref type="witness" target="account_of_a_tour_on_the_continent_msia_g2.php">MS IA, <hi rend="ital">g</hi>.2</ref></cell>\r\n                                <cell n="4" role="data"><ref type="witness" target="account_of_a_tour_on_the_continent_msia_g2.php">MS IA, <hi rend="ital">g</hi>.2</ref>, \r\n                                    <persName corresp="#JR">Ruskin</persName> Line Numbering</cell>\r\n                                <cell n="5" role="data"><ref type="witness" target="account_of_a_tour_on_the_continent_msviii.php">MS VIII &#x201C;Account&#x201D; draft</ref></cell>\r\n                                <cell n="6" role="data"><ref type="witness" target="account_of_a_tour_on_the_continent_msviii.php">MS VIII &#x201C;Account&#x201D; draft</ref>, \r\n                                    <persName corresp="#JR">Ruskin</persName>&#x02BC;s Line Numbering</cell>\r\n                                <cell n="7" role="data">MS VIII, <ref type="witness" target="account_of_a_tour_on_the_continent_msviii_contents.php">MS VIII, List of Proposed Additional Contents</ref></cell>\r\n                                <cell n="8" role="data"><ref type="witness" target="account_of_a_tour_on_the_continent_msix.php">MS IX fair copy</ref></cell>\r\n                                <cell n="9" role="data"><ref type="note" subtype="bibliographical" target="poems_1891_note.php">Ruskin, <hi rend="italic">Poems</hi> (1891)</ref></cell>\r\n                                <cell n="10" role="data"><ref type="note" subtype="bibliographical" target="library_edition_note.php">Ruskin, <hi rend="italic">Works</hi></ref>, vol. 2 (1903)</cell>\r\n                            </row>\r\n                            <row role="data">\r\n                                <cell n="1" role="data" xml:id="CALAISPOEMIA"><ref type="witness" target="calais_poem_msia_g1.php">&#x201C;Calais&#x201D; [poem]</ref></cell>\r\n                                <cell n="2" role="data">1&#x2013;24</cell>\r\n                                <cell n="3" role="data" xml:id="CALAISPROSEIA"><ref type="witness" target="calais_prose_msia_g2.php">&#x201C;Calais&#x201D; [prose]</ref></cell>\r\n                                <cell n="4" role="data">n/a</cell>\r\n                                <cell n="5" role="data" xml:id="OHARETHERESPIRITS"><ref type="witness" target="oh_are_there_spirits_can_there_be_poem_msviii.php">&#x201C;Oh are there spirits, can there be&#x201D; [poem]</ref></cell>\r\n                                <cell n="6" role="data">506&#x2013;19</cell>\r\n                                <cell n="7" role="data">"1 Andernacht"</cell>\r\n                                <cell n="8" role="data" xml:id="CALAISFISHERMENDRAWINGIX"><ref type="figure" target="calais_fishermen_drawing_msix.php">Calais Fishermen [drawing]</ref></cell>\r\n                                <cell n="9" role="data" xml:id="ACCOUNTTITLE1891"><ref type="witness" target="account_of_a_tour_on_the_continent_title_1891.php">Title of work, with epigraph from <hi rend="italic">Praeterita</hi></ref> \r\n                                    (<ref type="bibliography" target="bibliography.php#POEMS4D1891"><hi rend="italic">Poems</hi> [4<hi rend="superscript">o</hi>, 8<hi rend="superscript">o</hi>, 1891]</ref>, 1:119)</cell>\r\n                                <cell n="10" role="data" xml:id="ACCOUNTTITLE1903"><ref type="witness" target="account_of_a_tour_on_the_continent_title_le.php">Title of work</ref> \r\n                                    (<ref type="bibliography" target="bibliography.php#JRWORKSLE">Ruskin, <hi rend="italic">Works</hi></ref>, 2:340)</cell>\r\n                            </row>\r\n                            <row role="data">\r\n                                <cell n="1" role="data" xml:id="CASSELPOEMIA"><ref type="witness" target="cassel_poem_msia_g1.php">[&#x201C;Cassel&#x201D;] [poem]</ref></cell>\r\n                                <cell n="2" role="data">25&#x2013;62</cell>\r\n                                <cell n="3" role="data"><ref type="witness" target="passing_the_alps_msia_g2.php">&#x201C;Passing the Alps&#x201D; [poem]</ref></cell>\r\n                                <cell n="4" role="data">324&#x2013;59</cell>\r\n                                <cell n="5" role="data" xml:id="SOURCEOFTHEARVERON"><ref type="witness" target="source_of_the_arveron_prose_msviii.php">&#x201C;Source of the Arveron&#x201D;</ref> [prose]</cell>\r\n                                <cell n="6" role="data">n/a</cell>\r\n                                <cell n="7" role="data">to come</cell>\r\n                                <cell n="8" role="data"><ref type="witness" target="account_of_a_tour_on_the_continent_msix_calais_title.php">&#x201C;Calais&#x201D; [title of section]</ref>; \r\n                                    <ref type="witness" target="calais_poem_msix.php">&#x201C;The sands are in the sunlight sleeping&#x201D; [&#x201C;Calais,&#x201D; poem]</ref></cell>\r\n                                <cell n="9" role="data"><ref type="witness" target="calais_poem_1891.php">&#x201C;Calais.&#x201D; [poem]</ref> (<ref type="bibliography" target="bibliography.php#POEMS4D1891"><hi rend="italic">Poems</hi> [4<hi rend="superscript">o</hi>, 8<hi rend="superscript">o</hi>, 1891]</ref>, 1:119&#x2013;20)</cell>\r\n                                <cell n="10" role="data"><ref type="witness" target="account_of_a_tour_on_the_continent_le_calais_title.php">&#x201C;Calais&#x201D; [title of section]</ref>; \r\n                                    <ref type="witness" target="calais_poem_le.php">&#x201C;The sands are in the sunlight sleeping&#x201D; [&#x201C;Calais,&#x201D; poem]</ref> \r\n                                    (<ref type="bibliography" target="bibliography.php#JRWORKSLE">Ruskin, <hi rend="italic">Works</hi></ref>, 2:341)</cell>\r\n                            </row>\r\n                            <row role="data">\r\n                                <cell n="1" role="data" xml:id="LILLEPOEMIA"><ref type="witness" target="lille_poem_msia_g1.php">&#x201C;Lille&#x201D; [poem]</ref></cell>\r\n                                <cell n="2" role="data">63&#x2013;112</cell>\r\n                                <cell n="3" role="data"><ref type="witness" target="milan_cathedral_msia_g2.php">&#x201C;Milan Cathedral&#x201D; [poem]</ref></cell>\r\n                                <cell n="4" role="data">360&#x2013;77</cell>\r\n                                <cell n="5" role="data"><ref type="witness" target="i_woke_to_hear_the_lullaby_poem_msviii.php">&#x201C;I woke to hear the lullaby&#x201D; [&#x201C;The Arve at Chamouni&#x201D;] [poem]</ref></cell>\r\n                                <cell n="6" role="data">520&#x2013;64</cell>\r\n                                <cell n="7" role="data">to come</cell>\r\n                                <cell n="8" role="data"><ref type="figure" target="calais_fishermen_with_telescope_drawing_msix.php">Calais Fishermen with Telescope [drawing]</ref></cell>\r\n                                <cell n="9" role="data"><ref type="witness" target="cassel_poem_1891.php">&#x201C;Cassel.&#x201D; [poem]</ref> (<ref type="bibliography" target="bibliography.php#POEMS4D1891"><hi rend="italic">Poems</hi> [4<hi rend="superscript">o</hi>, 8<hi rend="superscript">o</hi>, 1891]</ref>, 1:120&#x2013;21)</cell>\r\n                                <cell n="10" role="data"><ref type="witness" target="calais_prose_le.php">&#x201C;How much has been said of Calais&#x201D; [&#x201C;Calais,&#x201D; prose]</ref> (<ref type="bibliography" target="bibliography.php#JRWORKSLE">Ruskin, <hi rend="italic">Works</hi></ref>, 2:341&#x2013;42)</cell>\r\n                            </row>\r\n                            <row role="data">\r\n                                <cell n="1" role="data" xml:id="BRUSSELSPOEMIA"><ref type="witness" target="brussels_poem_msia_g1.php">&#x201C;Brussels&#x201D; [poem]</ref></cell>\r\n                                <cell n="2" role="data">113&#x2013;73</cell>\r\n                                <cell n="3" role="data"><ref type="witness" target="andernacht_msia_g2.php">&#x201C;Andernacht&#x201D; [poem]</ref></cell>\r\n                                <cell n="4" role="data">380&#x2013;403</cell>\r\n                                <cell n="5" role="data"><ref type="witness" target="andernacht_prose_msviii.php">&#x201C;Andernacht&#x201D;</ref> [prose]</cell>\r\n                                <cell n="6" role="data">n/a</cell>\r\n                                <cell n="7" role="data">to come</cell>\r\n                                <cell n="8" role="data"><ref type="figure" target="calais_pier_drawing_msix.php">Calais Pier [drawing]</ref></cell>\r\n                                <cell n="9" role="data"><ref type="witness" target="lille_poem_1891.php">&#x201C;Lille.&#x201D; [poem]</ref> (<ref type="bibliography" target="bibliography.php#POEMS4D1891"><hi rend="italic">Poems</hi> [4<hi rend="superscript">o</hi>, 8<hi rend="superscript">o</hi>, 1891]</ref>, 1:122&#x2013;23)</cell>\r\n                                <cell n="10" role="data"><ref type="witness" target="account_of_a_tour_on_the_continent_le_cassel_title.php">&#x201C;Cassel&#x201D; [title of section]</ref>; \r\n                                    <ref type="witness" target="cassel_poem_le.php">&#x201C;The way was long, and yet &#x02BC;twas sweet&#x201D; [&#x201C;Cassel,&#x201D; poem]</ref> (<ref type="bibliography" target="bibliography.php#JRWORKSLE">Ruskin, <hi rend="italic">Works</hi></ref>, 2:342&#x2013;43)</cell>\r\n                            </row>\r\n                            <row role="data">\r\n                                <cell n="1" role="data" xml:id="THEMEUSEPOEMIA"><ref type="witness" target="the_meuse_poem_msia_g1.php">&#x201C;The Meuse&#x201D; [&#x201C;Cassel,&#x201D; poem]</ref></cell>\r\n                                <cell n="2" role="data">174&#x2013;209</cell>\r\n                                <cell n="3" role="data"><ref type="witness" target="st_goar_msia_g2.php">&#x201C;St Goar&#x201D; [poem]</ref></cell>\r\n                                <cell n="4" role="data">405&#x2013;29</cell>\r\n                                <cell n="5" role="data"><ref type="witness" target="st_goar_prose_msviii.php">&#x201C;St. Goar is the least and sweetest place&#x201D; [prose]</ref></cell>\r\n                                <cell n="6" role="data">n/a</cell>\r\n                                <cell n="7" role="data">to come</cell>\r\n                                <cell n="8" role="data"><ref type="witness" target="calais_prose_msix.php">&#x201C;How much has been said of Calais&#x201D; [&#x201C;Calais,&#x201D; prose]</ref></cell>\r\n                                <cell n="9" role="data"><ref type="witness" target="brussels_poem_1891.php">&#x201C;Brussels.&#x201D; [poem]</ref> (<ref type="bibliography" target="bibliography.php#POEMS4D1891"><hi rend="italic">Poems</hi> [4<hi rend="superscript">o</hi>, 8<hi rend="superscript">o</hi>, 1891]</ref>, 1:123&#x2013;26)</cell>\r\n                                <cell n="10" role="data"><ref type="witness" target="cassel_prose_le.php">&#x201C;When shall we get up this hill&#x201D; [&#x201C;Cassel,&#x201D; prose]</ref> (<ref type="bibliography" target="bibliography.php#JRWORKSLE">Ruskin, <hi rend="italic">Works</hi></ref>, 2:343&#x2013;44)</cell>\r\n                            </row>\r\n                            <row role="data">\r\n                                <cell n="1" role="data"></cell>\r\n                                <cell n="2" role="data"></cell>\r\n                                <cell n="3" role="data"></cell>\r\n                                <cell n="4" role="data"></cell>\r\n                                <cell n="5" role="data" xml:id="THEREISACHARMEDPEACE"><ref type="witness" target="there_is_a_charmed_peace_that_aye_poem_msviii.php">&#x201C;There is a charmed peace, that aye&#x201D; [&#x201C;The Alps from Schaffhausen&#x201D;] [poem]</ref></cell>\r\n                                <cell n="6" role="data">565&#x2013;614</cell>\r\n                                <cell n="7" role="data">to come</cell>\r\n                                <cell n="8" role="data"><ref type="figure" target="calais_square_drawing_msix.php">Calais Square [drawing]</ref></cell>\r\n                                <cell n="9" role="data"><ref type="witness" target="the_meuse_poem_1891.php">&#x201C;The Meuse.&#x201D; [poem]</ref> (<ref type="bibliography" target="bibliography.php#POEMS4D1891"><hi rend="italic">Poems</hi> [4<hi rend="superscript">o</hi>, 8<hi rend="superscript">o</hi>, 1891]</ref>, 1:126&#x2013;27)</cell>\r\n                                <cell n="10" role="data"><ref type="witness" target="account_of_a_tour_on_the_continent_le_lille_title.php">&#x201C;Lille&#x201D; [title of section]</ref>; \r\n                                    <ref type="witness" target="lille_poem_le.php">&#x201C;Oh, red the blushing east awoke&#x201D; [&#x201C;Lille,&#x201D; poem]</ref> (<ref type="bibliography" target="bibliography.php#JRWORKSLE">Ruskin, <hi rend="italic">Works</hi></ref>, 2:344&#x2013;45)</cell>\r\n                            </row>\r\n                            <row role="data">\r\n                                <cell n="1" role="data"></cell>\r\n                                <cell n="2" role="data"></cell>\r\n                                <cell n="3" role="data"></cell>\r\n                                <cell n="4" role="data"></cell>\r\n                                <cell n="5" role="data" xml:id="VIAMALA"><ref type="witness" target="via_mala_poem_msviii.php">&#x201C;Via Mala&#x201D; [poem]</ref></cell>\r\n                                <cell n="6" role="data">615&#x2013;46</cell>\r\n                                <cell n="7" role="data">to come</cell>\r\n                                <cell n="8" role="data"><ref type="figure" target="cassel_hill_drawing_msix.php">Cassel Hill [drawing]</ref></cell>\r\n                                <cell n="9" role="data"><ref type="witness" target="cologne_poem_1891.php">&#x201C;Cologne.&#x201D; [poem]</ref> (<ref type="bibliography" target="bibliography.php#POEMS4D1891"><hi rend="italic">Poems</hi> [4<hi rend="superscript">o</hi>, 8<hi rend="superscript">o</hi>, 1891]</ref>, 1:127&#x2013;28)</cell>\r\n                                <cell n="10" role="data"><ref type="witness" target="lille_prose_le.php">&#x201C;Passporte, monsieur, s&#x02BC;il vous plait&#x201D; [&#x201C;Lille,&#x201D; prose]</ref> \r\n                                    (<ref type="bibliography" target="bibliography.php#JRWORKSLE">Ruskin, <hi rend="italic">Works</hi></ref>, 2:345)</cell>\r\n                            </row>\r\n                            <row role="data">\r\n                                <cell n="1" role="data"></cell>\r\n                                <cell n="2" role="data"></cell>\r\n                                <cell n="3" role="data"></cell>\r\n                                <cell n="4" role="data"></cell>\r\n                                <cell n="5" role="data" xml:id="SPLUGEN"><ref type="witness" target="splugen_poem_msviii.php">&#x201C;Splugen&#x201D; [poem]</ref></cell>\r\n                                <cell n="6" role="data">647&#x2013;95</cell>\r\n                                <cell n="7" role="data">to come</cell>\r\n                                <cell n="8" role="data"><ref type="witness" target="account_of_a_tour_on_the_continent_msix_cassel_title.php">&#x201C;Cassel&#x201D; [title of section]</ref>; \r\n                                    <ref type="witness" target="cassel_poem_msix.php">&#x201C;The way was long, and yet &#x02BC;twas sweet&#x201D; [&#x201C;Cassel,&#x201D; poem]</ref></cell>\r\n                                <cell n="9" role="data"><ref type="witness" target="andernach_poem_1891.php">&#x201C;Andernach.&#x201D; [poem]</ref> (<ref type="bibliography" target="bibliography.php#POEMS4D1891"><hi rend="italic">Poems</hi> [4<hi rend="superscript">o</hi>, 8<hi rend="superscript">o</hi>, 1891]</ref>, 1:128&#x2013;29); \r\n                                    <ref type="witness" target="andernach_first_sketch_poem_1891.php">&#x201C;First Sketch of &#x2018;Andernach.&#x2019;&#x201D; [poem]</ref>, based on witnesses in MS IA, g.2, and/or MS IX (favoring variants in the latter0, printed in n. 30 \r\n                                    (<ref type="bibliography" target="bibliography.php#POEMS4D1891"><hi rend="italic">Poems</hi> [4<hi rend="superscript">o</hi>, 1891]</ref>,\r\n                                    1:281&#x2013;82; <ref type="bibliography" target="bibliography.php#POEMS8D1891"><hi rend="italic">Poems</hi> [8<hi rend="superscript">o</hi>, 1891]</ref>, 1:283)</cell>\r\n                                <cell n="10" role="data"><ref type="witness" target="account_of_a_tour_on_the_continent_le_brussels_title.php">&#x201C;Brussels&#x201D; [title of section]</ref>; \r\n                                    <ref type="witness" target="brussels_poem_le.php">&#x201C;The racking clouds were fleeting fast&#x201D;[&#x201C;Brussels,&#x201D; poem]</ref> \r\n                                    (<ref type="bibliography" target="bibliography.php#JRWORKSLE">Ruskin, <hi rend="italic">Works</hi></ref>, 2:346&#x2013;47)</cell>\r\n                            </row>\r\n                            <row role="data">\r\n                                <cell n="1" role="data"></cell>\r\n                                <cell n="2" role="data"></cell>\r\n                                <cell n="3" role="data"><